mapper.select:
select s.*,d.departmentname,c.classname from student s,department d,class c
where s.departmentid=d.departmentid
and s.classid=c.classid and studentid = #{studentId}

mapper.update:
update student set studentname = #{studentName} sex = #{sex},nation = #{nation},birthday = #{birthday},address = #{address},
ismarried = #{isMarried},tel = #{tel},school = #{school},major = #{major},
remark = #{remark},idcard = #{idCard} where studentid = #{studentId}
